#include <iostream>
using namespace std;
//строка для победы компа
string x = " WON!";
//строка для победы игрока 1
//srting x = " WON!";
//строка для победы игрока 2
//string x = " WON!";
void disp_call(string now){
    system("clear");
    int counter = 0;
    int disp_res_x = 60, disp_res_y = 16;
    for(int i = 0; i < disp_res_y; i++){
        for(int j = 0; j < disp_res_x; j++){
            char nowx = now[counter];
            if(nowx == 't'){
                cout << "-";
            }
            else if (nowx == 'e'){
                cout << "·";
            }
            else if (nowx == 'z'){
                cout<<"*";
            }
            else if (nowx == 'u'){
                cout<<"X";
            }
            else if (nowx == 'm'){
                cout << "о";
            }
            else if(nowx == 'r'){
                cout << "☆";
            }
            else{
                cout << nowx;
            }
            counter++;
        }
        cout << endl;
    }
}

int main()
{
    disp_call(x);
    //cout<< " "
}
